INSERT INTO `wp_posts` VALUES ('455', '1', '2016-01-19 21:16:43', '2016-01-19 13:16:43', '<h2>70寸液晶监视器 高清高清显示器</h2>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>最高分辨率/Max resolution ratio</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>1920×1080</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>视角/Viewing angle</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>178°（水平）；178°（垂直）</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>颜色/Color</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>16.7M彩色</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>亮度/Brightness</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>700cd/m2</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>对比度/Contrast ratio</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>1450：1</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>讯号响应时间/Response tme</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>5ms</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>可视面积/Visable area</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>1549.44mm(H)×871.56 mm(V)</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>图像处理/Image processing</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>采用3-D数字梳状滤波器，采用MstarACE-5自动彩色及图像增强引擎，改善图像对比度，细节、肤色、边缘等</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>接口/Interface</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>HDMI、DVI、VGA、AV-IN×4、SV-Y、SC-C、YPB、PR、USB2.0×2、AUDIO-inL&amp;R、AUDIO-outL&amp;R</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>工业级散热风扇/Industrial cooling fan</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>YES</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>外壳材料/Shell material</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>钣金结构，铝型材面框</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>电源规格/Power supply spec</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>电压范围：100VAC to 240VAC</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>喇叭功耗/Speaker power</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>10W(选配)</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>最大功耗/Max consumption</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>≤800W</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>待机功耗/Stand-by consumption</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>≤1W</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>工作温度/Operating temperature</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>0°C-40°C</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>工作湿度/Operating humidity</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>10%-90%无凝露</h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"247\">\r\n<h3>存贮温度/Storage temperature</h3>\r\n</td>\r\n<td width=\"359\">\r\n<h3>－20°C-60°C</h3>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '液晶监视器-YC70', '', 'publish', 'open', 'open', '', '%e6%b6%b2%e6%99%b6%e7%9b%91%e8%a7%86%e5%99%a8-yc70-1', '', '', '2016-01-19 21:17:04', '2016-01-19 13:17:04', '', '0', 'http://www.yiichen.cn/?p=455', '0', 'post', '', '0');
